head.script:
DekiWiki.subscribe(args.subscribe, null, function(c, m, d) {
  var result = m;
  if(d.copy) {
    result = { };
    for(var key in d.copy) result[key] = m[d.copy[key]];
  }
  if(d.add) for(var key in d.add) result[key] = d.add[key];
  DekiWiki.publish(args.publish, result);
}, { copy: args.copy, add: args.add });

DekiWiki.subscribe(args.subscribe, null, function(c, m, d) {
  if(DekiWiki.hasValue(m[args.field]) && d.regex.test(m[args.field])) {
    DekiWiki.publish(args.publish, m);
  }
}, { regex: new RegExp(args.pattern) });
